const cal=function(num1,num2,opr){
    if(opr==="add"){
        return num1+num2;
    }
    else if(opr==="sub"){
        return num1-num2;
    }
    else if(opr==="mod") {
        return num1%num2;
    }
    else if(opr==="div") {
        return num1/num2
    }
    else if(opr==="mul"){
        return num1*num2
    }
}

module.exports={
    cal
}


