const readline = require("readline");
const chalk = require("chalk");
const prompt = require("prompt-sync")();

const rl = readline.createInterface({
    input: process.stdin,
    output: process.stdout,
});

rl.question(`Enter a number to match 1 to 100:` , (Number) =>{
    var random = Math.floor(Math.random() * 100) + 1;
    while(Number != random){
        if(Number > random){
            console.log(chalk.red(`Number is too high`));
            Number = prompt(`Enter a number to match 1 to 100:`);
        }
        else{
            console.log(chalk.red(`Number is too low`));
            Number = prompt(`Enter a number to match 1 to 100:`);
        }
    }
    if (Number == random){
        console.log(chalk.green(`You won`));
    }
    else{
        console.log(chalk.red(`You lose`));
    }
    console.log(chalk.blue(`Random Number is ${random}`));
    rl.close();
});

module.exports = rl;